import Edit from "./components/edit";
import Create from "./components/create";
import RecordList from "./components/recordList";

/**** Database ****/
const artwalkDB = require('./artwalk_db')(mongoose);

mongoose.connect ("mongodb+srv://frankild:pc83087pc@artwalkkonzept.rnrwp.mongodb.net/ArtWalk_db")

const App = () => {
  return (
    <div>
      <Navbar />
      <Route exact path="/api/">
        <RecordList />
      </Route>
      <Route path="/api/edit/:id" component={Edit} />
      <Route path="/api/create">
        <Create />
      </Route>
    </div>
  );
};

export default App;
