import React, { Component } from "react";
// This will require to npm install axios
import axios from 'axios';

export default class Create extends Component {
  // This is the constructor that stores the data.
  constructor(props) {
    super(props);

    this.onChangeArticleTitle = this.onChangeArticleTitle.bind(this);
    this.onChangeArticleDescription = this.onChangeArticleDescription.bind(this);
    this.onSubmit = this.onSubmit.bind(this);

    this.state = {
      article_title: "",
      article_description: "",
    };
  }

  // These methods will update the state properties.
  onChangeArticleTitle(e) {
    this.setState({
      article_title: e.target.value,
    });
  }

  onChangeArticleDescription(e) {
    this.setState({
      article_description: e.target.value,
    });
  }

// This function will handle the submission.
onSubmit(e) {
  e.preventDefault();

  // When post request is sent to the create url, axios will add a new record(newarticle) to the database.
  const newarticle = {
    article_title: this.state.article_title,
    article_description: this.state.article_description,
  };

  axios
    .post("http://localhost:5000/record/add", newarticle)
    .then((res) => console.log(res.data));

  // We will empty the state after posting the data to the database
  this.setState({
    article_title: "",
    article_description: "",
  });
}

  // This following section will display the form that takes the input from the user.
  render() {
    return (
      <div style={{ marginTop: 20 }}>
        <h3>Create New Record</h3>
        <form onSubmit={this.onSubmit}>
          <div className="form-group">
            <label>Title of the article: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.article_title}
              onChange={this.onChangeArticleTitle}
            />
          </div>
          <div className="form-group">
            <label>Article's description: </label>
            <input
              type="text"
              className="form-control"
              value={this.state.article_description}
              onChange={this.onChangeArticleDescription}
            />
          </div>
          <div className="form-group">
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                title="priorityOptions"
                id="priorityLow"
                value="Intern"
                checked={this.state.article_level === "Intern"}
                onChange={this.onChangeArticleLevel}
              />
              <label className="form-check-label">Intern</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                title="priorityOptions"
                id="priorityMedium"
                value="Junior"
                checked={this.state.article_level === "Junior"}
                onChange={this.onChangeArticleLevel}
              />
              <label className="form-check-label">Junior</label>
            </div>
            <div className="form-check form-check-inline">
              <input
                className="form-check-input"
                type="radio"
                title="priorityOptions"
                id="priorityHigh"
                value="Senior"
                checked={this.state.article_level === "Senior"}
                onChange={this.onChangeArticleLevel}
              />
              <label className="form-check-label">Senior</label>
            </div>
          </div>
          <div className="form-group">
            <input
              type="submit"
              value="Create article"
              className="btn btn-primary"
            />
          </div>
        </form>
      </div>
    );
  }
}
